               this[i] = model[i];
             }
         }

         this._node = this.buildNode(node);

     }

     buildNode = function(node){

        if(node.node){
            var _node = {
                ...node.node.properties,
                type: node.node.labels[0]
            }
        }else{
            var _node = {
                ...node,
                type: this.modelName
            }
        }

        return _node;
    }

    save = function(){

        const safeProps = this.schema.validate(this._node);

        const Model = this.model;

        var query = [
            'CREATE (node:'+this.modelName+' {props})',
            'RETURN node',
        ].join('\n');

        var params = {
            props: safeProps
        };

        return this.db.cypherAsync({ query: query, params: params })
            .then(response => {
                return new Node(Model, response[0]);
            })

    }



